import React, { useEffect, useState } from 'react';
import axios from "axios";

const Result = () => {

    const [document,setDocument]=useState([]);
   
    useEffect(()=>{
        getDocument();
     
    },[])

    const handleDeleteDoc=(e,id)=>{
       
        axios.delete("documentsMysql/"+id)
             .then(res=>{console.log(res)})
             .catch((err) => {console.log(err) });
             
             getDocument();
    }

    const getDocument=()=>{
        axios.get("documentsMysql").then((res)=>setDocument(res.data));
    }
    return (

                    <div>
                    RESULTAT

                    <nav className="navbar navbar-light bg-light">
                        <div className="container-fluid">
                            <form className="d-flex">
                            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
                            <button className="btn btn-outline-success" type="submit">Search</button>
                            </form>
                        </div>
                    </nav>

                    <table className="table ">
                            <thead>
                                <tr>
                                <th scope="col">#</th>
                                <th scope="col">Titre</th>
                                <th scope="col">Auteur</th>
                                <th scope="col">Pdf</th>
                                <th scope="col">Interaction</th>
                                </tr>
                            </thead>
                            <tbody>
                            {document.map(doc=> 
                                <tr key={doc.id}>
                                    <th scope="row">{doc.id}</th>
                                    <td>  {doc.titre}</td>
                                    <td> {doc.auteur}</td>
                                    <td> {doc.nom_pdf}</td>
                                    <td>
                                    <div className="btn-group" role="group" aria-label="Basic example">
                                        <button type="button" className="btn btn-primary">details</button>
                                        <button type="button" className="btn btn-primary">modifier</button>
                                        <button type="button" className="btn btn-primary" onClick={(e)=>handleDeleteDoc(e,doc.id)}>Supprimer</button>
                                    </div>    
                                    </td>
                               
                                </tr>
                                   )}
                            </tbody>
                    </table>
                                           
                    
                  

                  
                </div>
              
    );
};

export default Result;
