import React, { useEffect, useState } from 'react';
import axios from "axios";

const Result = () => {

    const [document,setDocument]=useState([]);
   // console.log("test");
    useEffect(()=>{
        axios.get("documentsMysql").then((res)=>setDocument(res.data))
     
    },[])

    const handleDeleteDoc=(e,id)=>{
        console.log("id: "+id);
        axios.delete("documentsMysql/"+id)
             .then(res=>{console.log(res)})
             .catch((err) => {console.log(err) });
    }

    return (

                    <div>
                    RESULTAT

                    <div className="card">

                    <h5 className="card-header">Featured</h5>
                        <div className="card-body">
                            <h5 className="card-title">Special title treatment</h5>
                            <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
                            <a href="#" className="btn btn-primary">Go somewhere</a>
                        </div>
                    </div>
                    <br></br>

                    <table className="table ">
                            <thead>
                                <tr>
                                <th scope="col">#</th>
                                <th scope="col">Titre</th>
                                <th scope="col">Auteur</th>
                                <th scope="col">Pdf</th>
                                <th scope="col">Interaction</th>
                                </tr>
                            </thead>
                            <tbody>
                            {document.map(doc=> 
                                <tr key={doc.id}>
                                    <th scope="row">{doc.id}</th>
                                    <td>  {doc.titre}</td>
                                    <td> {doc.auteur}</td>
                                    <td> {doc.nom_pdf}</td>
                                    <td>
                                    <div className="btn-group" role="group" aria-label="Basic example">
                                        <button type="button" className="btn btn-primary">details</button>
                                        <button type="button" className="btn btn-primary">modifier</button>
                                        <button type="button" className="btn btn-primary" onClick={(e)=>handleDeleteDoc(e,doc.id)}>Supprimer</button>
                                    </div>    
                                    </td>
                               
                                </tr>
                                   )}
                            </tbody>
                    </table>
                                           
                    
                  

                  
                </div>
              
    );
};

export default Result;
